package main.java.com.zxw.leetcode;

import java.util.Arrays;

/**
 * ClassName: code392
 * Description:
 *给定字符串 s 和 t ，判断 s 是否为 t 的子序列。
 *
 * 你可以认为 s 和 t 中仅包含英文小写字母。字符串 t 可能会很长（长度 ~= 500,000），而 s 是个短字符串（长度 <=100）。
 *
 * 字符串的一个子序列是原始字符串删除一些（也可以不删除）字符而不改变剩余字符相对位置形成的新字符串。（例如，"ace"是"abcde"的一个子序列，而"aec"不是）。
 *
 * @author zxw
 * @date 2020/7/1 7:33 下午
 * @since JDK 1.8
 */
public class code392 {



    public static void main(String[] args) {
        String s = "leeeeetcode";
        String t = "";
        String s1 = "axc";
        String t1 = "ahbgdc";
        System.out.println(isSubsequence3(s,t));


    }


    /**
     * 2020年11月28日22:10:31 更新解法
     * 使用双指针算法
     * @param s
     * @param t
     * @return
     */
    public boolean isSubsequence2(String s, String t) {

        if (s.length() == 0){
            return true;
        }
        if (t.length() == 0){
            return false;
        }
        int tl = 0,tr = t.length()-1;
        int sl = 0,sr = s.length() - 1;
        while (tl <= tr && sl <= sr){
            if (s.charAt(sl) == t.charAt(tl)){
                tl++;sl++;
            }else{
                tl++;
            }
            if (s.charAt(sr) == t.charAt(tr)){
                tr--;sr--;
            }else{
                tr--;
            }
        }
        return sl > sr;


    }

    /**
     * 动态规划
     * dp一维数组存储的是字符串s对应的字符 在 字符串t中的第一次出现的位置
     * @param s
     * @param t
     * @return
     */
    public static boolean isSubsequence(String s, String t) {
         if (s ==null || s.length() == 0){
             return true;
         }
         int[] dp = new int[s.length()];
         Arrays.fill(dp,-1);

         for (int i = 0; i < t.length(); i++) {
             if (t.charAt(i) ==  s.charAt(0)){
                 dp[0] = i;
             }
        }
         if (dp[0] != -1) {
             int j = dp[0] + 1;
             for (int i = 1; i < s.length(); i++) {
                 for (; j < t.length(); j++) {
                     if (t.charAt(j) == s.charAt(i) && dp[i - 1] != -1) {
                         dp[i] = j;
                         break;
                     }
                 }
                 if (dp[i] != -1) {
                     j = dp[i] + 1;
                 }
             }
         }
         return dp[s.length()-1] == -1 ? false : true;
    }

    /**
     * 添加剪枝的暴力解法
     * 两层循环， 如果在t的x下标中找到当前元素，就从x之后的位置开始寻找剩下的元素
     * 如果没找到，则返回false
     * @param s
     * @param t
     * @return
     */
    public static boolean isSubsequence3(String s, String t) {

        if (s == null || s.length() == 0){
            return true;
        }
        int j = 0;
        for (int i = 0; i < s.length(); i++){
            // 存储当前s元素在t字符串中的位置
            int index = -1;
            for (;j < t.length();j++){
                if (s.charAt(i) == t.charAt(j)){
                    index = j++;
                    break;
                }
            }
            if (index == -1){
                return false;
            }
        }
        return true;
    }



    }
